DROP procedure IF EXISTS `union_reporting`.`SELECT_CUSTOMER`;
#
CREATE PROCEDURE `union_reporting`.`SELECT_CUSTOMER`( IN request_id VARCHAR(11), IN request_user_id VARCHAR(11) ) BEGIN IF NOT EXISTS (SELECT * FROM union_reporting.users where id = request_user_id AND (users.unit_coordinator = 1 OR users.head = 1 OR users.id = 1 OR users.admin = 1 OR users.manager = 1 OR users.account_manager = 1)) THEN signal sqlstate '23515'; END IF;
IF NOT EXISTS (SELECT id FROM union_reporting.users where id = request_user_id AND (users.unit_coordinator = 1 OR users.head = 1 OR users.id = 1 OR users.account_manager = 1)) THEN SELECT id, ANY_VALUE(name) as name, ANY_VALUE(coordinator_id) as coordinator_id, ANY_VALUE(created) as created, sum(cus.proj_id IS NOT NULL) as projects_count FROM ( SELECT customer.*, projects.id as proj_id FROM union_reporting.customer LEFT JOIN union_reporting.projects ON customer.id=projects.customer_id UNION SELECT customer.*, projects.id as proj_id FROM union_reporting.customer RIGHT JOIN union_reporting.projects ON customer.id=projects.customer_id ) as cus WHERE cus.id IS NOT NULL AND (request_id = '' OR id=request_id) group by cus.id; else SELECT id, ANY_VALUE(name) as name, ANY_VALUE(coordinator_id) as coordinator_id, ANY_VALUE(accounting) as accounting, ANY_VALUE(account_manager_id) as account_manager_id, ANY_VALUE(created) as created, sum(cus.proj_id IS NOT NULL) as projects_count FROM ( SELECT customer.*, projects.id as proj_id FROM union_reporting.customer LEFT JOIN union_reporting.projects ON customer.id=projects.customer_id UNION SELECT customer.*, projects.id as proj_id FROM union_reporting.customer RIGHT JOIN union_reporting.projects ON customer.id=projects.customer_id ) as cus WHERE cus.id IS NOT NULL AND (request_id = '' OR id=request_id) AND (cus.account_manager_id = request_user_id OR 1 = (SELECT head From union_reporting.users WHERE id = request_user_id) OR 1 = (SELECT unit_coordinator From union_reporting.users WHERE id = request_user_id) OR 1 = request_user_id ) group by cus.id;
END IF; END
