CREATE PROCEDURE `union_reporting`.`SELECT_PROJECT`( IN projectName VARCHAR(500), IN projectId VARCHAR(10), IN userId VARCHAR(11), IN request_customer_id VARCHAR(11) ) BEGIN SELECT DISTINCT proj.* FROM union_reporting.projects as proj LEFT JOIN union_reporting.user_roles as us_r ON proj.id = us_r.project_id LEFT JOIN union_reporting.customer as cus_r ON proj.customer_id = cus_r.id WHERE (projectId = '' OR proj.id=projectId) AND (projectName = '' OR proj.name=projectName) AND (request_customer_id = '' OR proj.customer_id=request_customer_id) AND (userId = '' OR user_id=userId OR 1=(SELECT admin From union_reporting.users WHERE id = userId) OR 1=(SELECT head From union_reporting.users WHERE id = userId) OR 1=(SELECT unit_coordinator From union_reporting.users WHERE id = userId) OR 1=(SELECT manager From union_reporting.users WHERE id = userId) OR 1=(SELECT auditor From union_reporting.users WHERE id = userId) OR 1=(SELECT audit_admin From union_reporting.users WHERE id = userId) OR account_manager_id = userId ); END
