CREATE PROCEDURE `union_reporting`.`INSERT_CUSTOMER`( IN request_id VARCHAR(11), IN request_name VARCHAR(150), IN request_coordinator_id VARCHAR(11), IN request_accounting VARCHAR(1), IN request_account_manager_id VARCHAR(11), IN request_user_id VARCHAR(11) ) BEGIN IF NOT EXISTS (SELECT * FROM union_reporting.users where id = request_user_id AND (users.unit_coordinator = 1 OR users.head = 1 OR users.account_manager = 1)) THEN signal sqlstate '23515'; END IF;
IF NOT EXISTS (SELECT * FROM union_reporting.users where id = request_user_id AND (users.unit_coordinator = 1 OR users.head = 1)) THEN IF NOT EXISTS (SELECT * FROM union_reporting.customer where id = request_id AND account_manager_id = request_user_id) THEN signal sqlstate '23515'; END IF; END IF;
INSERT INTO union_reporting.customer (id, name, coordinator_id, accounting, account_manager_id) VALUES ( IF(request_id='',null,request_id), request_name, IF(request_coordinator_id='',null,request_coordinator_id), IF(request_accounting='',0,request_accounting), IF(request_account_manager_id='',null,request_account_manager_id) ) ON DUPLICATE KEY UPDATE name = IF(request_name='',name,request_name), coordinator_id = IF(request_coordinator_id='',coordinator_id,request_coordinator_id), accounting = IF(request_accounting='',accounting,request_accounting), account_manager_id = IF(request_account_manager_id='',account_manager_id,request_account_manager_id) ;
Select * from union_reporting.customer Where id=LAST_INSERT_ID(); END
