CREATE PROCEDURE `union_reporting`.`SELECT_PROJECT_USERS`( IN projectId VARCHAR(11), IN userId VARCHAR(11) ) BEGIN SELECT user_id, project_id, MAX(admin) as admin, MAX(manager) as manager, MAX(engineer) as engineer, MAX(viewer) as viewer FROM( SELECT user_roles.* FROM union_reporting.user_roles LEFT JOIN union_reporting.projects ON user_roles.project_id = projects.id UNION SELECT customer.account_manager_id as user_id, projects.id as project_id, 0 as admin, 0 as manager, 0 as engineer, 1 as viewer FROM union_reporting.projects LEFT JOIN union_reporting.customer ON projects.customer_id = customer.id WHERE customer.account_manager_id IS NOT NULL) as ur WHERE (projectId= '' OR project_id=projectId) AND (userId='' OR user_id = userId) GROUP BY user_id, project_id ; END
