CREATE PROCEDURE `union_reporting`.`SELECT_AUDIT`( In audit_id varchar(11), In audit_project_id varchar(11), In audit_status_id varchar(11), IN request_user_id VARCHAR(11) ) BEGIN
IF NOT EXISTS ( SELECT * FROM union_reporting.user_roles right join union_reporting.users on union_reporting.user_roles.user_id = id where id = request_user_id AND (users.manager = 1 OR users.auditor = 1 OR users.audit_admin = 1 OR users.account_manager = 1 OR users.unit_coordinator = 1 OR users.head = 1 OR (union_reporting.user_roles.project_id = IF(audit_project_id='', (Select project_id from union_reporting.audits where id=audit_id and (status_id = 4 OR status_id = 3)), audit_project_id) AND (user_roles.admin = 1 OR user_roles.manager = 1 OR user_roles.engineer = 1 OR user_roles.viewer = 1)))) THEN signal SQLSTATE '45000' SET MESSAGE_TEXT = 'You have no permissions!'; END IF;
SELECT * FROM union_reporting.audits WHERE (audit_id = '' OR id=audit_id) AND (audit_project_id = '' OR project_id=audit_project_id) AND (audit_status_id = '' OR status_id=audit_status_id); END
