CREATE PROCEDURE `union_reporting`.`SELECT_AUDIT_STATS`( IN request_user_id VARCHAR(11) ) BEGIN IF NOT EXISTS (SELECT * FROM union_reporting.user_roles right join union_reporting.users on union_reporting.user_roles.user_id = id where id = request_user_id AND (users.manager = 1 OR users.auditor = 1 OR users.audit_admin = 1 OR users.account_manager = 1 OR users.unit_coordinator = 1 OR users.head = 1)) THEN signal sqlstate '23515'; END IF;
select projects.*, submitted.result, submitted.id as last_submitted_id, submitted.submitted as last_submitted_date, created.service_type_id, created.id as last_created_id, created.due_date as last_created_due_date, created.status_id, audit_statuses.name as status_name from projects
LEFT JOIN ((SELECT a.* FROM `audits` a LEFT JOIN `audits` b ON a.project_id = b.project_id AND a.service_type_id = b.service_type_id AND a.created < b.created WHERE b.created is NULL) as created ) ON projects.id = created.project_id
LEFT JOIN ( (SELECT a.* FROM `audits` a LEFT JOIN `audits` b ON a.project_id = b.project_id AND a.service_type_id = b.service_type_id AND a.submitted < b.submitted WHERE b.submitted is NULL AND a.submitted is NOT NULL) as submitted ) ON projects.id = submitted.project_id AND submitted.service_type_id = created.service_type_id
LEFT JOIN audit_statuses ON created.status_id = audit_statuses.id;
END
