CREATE PROCEDURE `union_reporting`.`SELECT_TEST_STATS`( IN request_user_id VARCHAR(10), IN test_test_suite_id VARCHAR(10), IN test_project_id VARCHAR(10) ) BEGIN IF NOT EXISTS (SELECT * FROM union_reporting.user_roles right join union_reporting.users on union_reporting.user_roles.user_id = id where id = request_user_id AND (users.manager = 1 OR users.auditor = 1 OR users.audit_admin = 1 OR users.account_manager = 1 OR users.unit_coordinator = 1 OR users.head = 1 OR (union_reporting.user_roles.project_id = test_project_id AND (user_roles.admin = 1 OR user_roles.manager = 1 OR user_roles.engineer = 1 OR user_roles.viewer = 1)))) THEN signal sqlstate '23515'; END IF;
SELECT id, name, developer_id, total_runs, passed, failed, app_issue, autotest_issue, resolution_na FROM union_reporting.tests LEFT JOIN (SELECT tr.test_id, SUM(IF(final_result_id != 3, 1, 0)) AS total_runs, SUM(IF(final_result_id = 1, 1, 0)) AS failed, SUM(IF(final_result_id = 2, 1, 0)) AS passed, SUM(IF(color = 1, 1, 0)) AS app_issue, SUM(IF(color = 2, 1, 0)) AS autotest_issue, SUM(IF(color = 3 AND final_result_id != 2, 1, 0)) AS resolution_na FROM union_reporting.test_results tr INNER JOIN union_reporting.result_resolution AS rr ON test_resolution_id = rr.id GROUP BY test_id) AS result ON tests.id = result.test_id WHERE test_suite_id = test_test_suite_id;
END
