CREATE PROCEDURE `union_reporting`.`SELECT_TESTRESULT`( IN request_user_id VARCHAR(10), IN testresult_project_id VARCHAR(10), IN testresult_Id VARCHAR(10), IN testresult_test_id VARCHAR(10), IN testresult_final_result_id VARCHAR(10), IN testresult_comment VARCHAR(5000), IN testresult_test_run_id VARCHAR(10), IN testresult_resolution_id VARCHAR(10), IN testresult_debug VARCHAR(1), IN resultFailReason mediumtext ) BEGIN IF NOT EXISTS (SELECT * FROM union_reporting.user_roles right join union_reporting.users on union_reporting.user_roles.user_id = id where id = request_user_id AND (users.manager = 1 OR users.auditor = 1 OR users.audit_admin = 1 OR users.account_manager = 1 OR users.unit_coordinator = 1 OR users.head = 1 OR (union_reporting.user_roles.project_id = testresult_project_id AND (user_roles.admin = 1 OR user_roles.manager = 1 OR user_roles.engineer = 1 OR user_roles.viewer = 1)))) THEN signal sqlstate '23515'; END IF;
SELECT * FROM union_reporting.test_results as res WHERE (testresult_Id = '' OR id=testresult_Id) AND (testresult_project_id = '' OR project_id = testresult_project_id) AND (testresult_test_id = '' OR test_id=testresult_test_id) AND (testresult_final_result_id = '' OR final_result_id=testresult_final_result_id) AND (testresult_comment = '' OR comment=testresult_comment) AND (testresult_test_run_id = '' OR test_run_id=testresult_test_run_id) AND (testresult_resolution_id = '' OR test_resolution_id=testresult_resolution_id) AND (testresult_debug = '' OR debug=testresult_debug) AND (resultFailReason = '' OR REPLACE(REPLACE(REPLACE(REPLACE(fail_reason, '\r', ''), '\n', ''), CHAR(9), ''),' ','') LIKE CONCAT('%',REPLACE(REPLACE(REPLACE(REPLACE(resultFailReason, '\r', ''), '\n', ''), CHAR(9), ''),' ',''),'%')) ORDER BY start_date DESC ; END
