DROP procedure IF EXISTS `union_reporting`.`SELECT_TESTRUN_STATS`;
#
CREATE PROCEDURE `union_reporting`.`SELECT_TESTRUN_STATS`( IN request_user_id VARCHAR(10), IN testrun_Id VARCHAR(10), IN testrun_build_name VARCHAR(500), IN testrun_milestone_id VARCHAR(10), IN testrun_test_suite_id VARCHAR(10), IN testrun_project_id VARCHAR(10), IN testrun_execution_environment VARCHAR(500), IN testrun_debug VARCHAR(1) ) BEGIN IF NOT EXISTS (SELECT * FROM union_reporting.user_roles right join union_reporting.users on union_reporting.user_roles.user_id = id where id = request_user_id AND (users.manager = 1 OR users.auditor = 1 OR users.audit_admin = 1 OR users.account_manager = 1 OR users.unit_coordinator = 1 OR users.head = 1 OR (union_reporting.user_roles.project_id = testrun_project_id AND (user_roles.admin = 1 OR user_roles.manager = 1 OR user_roles.engineer = 1 OR user_roles.viewer = 1)))) THEN signal sqlstate '23515'; END IF;
SELECT trn.id, trn.test_suite_id, trn.start_time, trn.finish_time, sum(frs.color = 1) as failed, sum(frs.color = 5) as passed, sum(frs.color = 3) as not_executed, sum(frs.color = 2) as in_progress, sum(frs.color = 4) as pending, sum(frs.color != 0) as total, sum(rr.color = 1 AND frs.color != 5) as app_issue, sum(rr.color = 2 AND frs.color != 5) as warning, sum(rr.color = 3 AND frs.color != 5) as not_assigned, sum((rr.color = 4 OR rr.color = 5) AND frs.color != 5) as other
from union_reporting.test_runs as trn right join union_reporting.test_results as trs on trn.id=test_run_id left join union_reporting.final_results as frs on trs.final_result_id = frs.id left join union_reporting.result_resolution as rr on trs.test_resolution_id = rr.id
WHERE (testrun_Id= '' OR trn.id=testrun_Id) AND (testrun_build_name = '' OR build_name=testrun_build_name) AND (testrun_milestone_id = '' OR milestone_id=testrun_milestone_id) AND (testrun_test_suite_id = '' OR test_suite_id=testrun_test_suite_id) AND (testrun_project_id = '' OR trn.project_id=testrun_project_id) AND (testrun_execution_environment = '' OR execution_environment=testrun_execution_environment) AND (testrun_debug = '' OR trn.debug=testrun_debug)
GROUP BY trn.id ORDER BY finish_time DESC;
END
#
DROP procedure IF EXISTS `union_reporting`.`SELECT_TESTRUN`;
#
CREATE PROCEDURE `union_reporting`.`SELECT_TESTRUN`( IN request_user_id VARCHAR(10), IN testrun_Id VARCHAR(10), IN testrun_build_name VARCHAR(500), IN testrun_milestone_id VARCHAR(10), IN testrun_test_suite_id VARCHAR(10), IN testrun_project_id VARCHAR(10), IN testrun_execution_environment VARCHAR(500), IN testrun_debug VARCHAR(1) ) BEGIN IF NOT EXISTS (SELECT * FROM union_reporting.user_roles right join union_reporting.users on union_reporting.user_roles.user_id = id where id = request_user_id AND (users.manager = 1 OR users.auditor = 1 OR users.audit_admin = 1 OR users.account_manager = 1 OR users.unit_coordinator = 1 OR users.head = 1 OR (union_reporting.user_roles.project_id = testrun_project_id AND (user_roles.admin = 1 OR user_roles.manager = 1 OR user_roles.engineer = 1 OR user_roles.viewer = 1)))) THEN signal sqlstate '23515'; END IF;
SELECT runs.id, build_name, start_time, milestone_id, mil.name as milestone_name, test_suite_id, suite.name as testsuite_name, runs.project_id, execution_environment, finish_time, updated, author, debug
FROM union_reporting.test_runs as runs LEFT JOIN union_reporting.milestones as mil ON runs.milestone_id = mil.id LEFT JOIN union_reporting.test_suites as suite ON runs.test_suite_id = suite.id
WHERE (testrun_Id= '' OR runs.id=testrun_Id) AND (testrun_build_name = '' OR build_name=testrun_build_name) AND (testrun_milestone_id = '' OR milestone_id=testrun_milestone_id OR IF(testrun_milestone_id=0, milestone_id IS NULL, testrun_milestone_id = '')) AND (testrun_test_suite_id = '' OR test_suite_id=testrun_test_suite_id) AND (testrun_project_id = '' OR runs.project_id=testrun_project_id) AND (testrun_execution_environment = '' OR execution_environment=testrun_execution_environment) AND (testrun_debug = '' OR runs.debug=testrun_debug) ORDER BY finish_time DESC ; END
