ALTER TABLE `union_reporting`.`customer_account_members` ADD COLUMN `id` INT NOT NULL AUTO_INCREMENT FIRST, DROP PRIMARY KEY, ADD PRIMARY KEY (`id`, `customer_id`, `user_id`);
