ALTER TABLE `union_reporting`.`customer_account_members` ADD CONSTRAINT `member_customer` FOREIGN KEY (`customer_id`) REFERENCES `union_reporting`.`customer` (`id`) ON DELETE CASCADE ON UPDATE CASCADE, ADD CONSTRAINT `member_user` FOREIGN KEY (`user_id`) REFERENCES `union_reporting`.`users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE;
