DROP procedure IF EXISTS `union_reporting`.`INSERT_CUSTOMER_MEMBER`;
#
CREATE PROCEDURE `union_reporting`.`INSERT_CUSTOMER_MEMBER`( In request_customer_id varchar(11), In request_member_id varchar(11), In request_user_id varchar(11) ) BEGIN IF NOT EXISTS (SELECT * FROM union_reporting.users where id = request_user_id AND (users.unit_coordinator = 1 OR users.head = 1 OR users.account_manager = 1)) THEN signal sqlstate '23515'; END IF;
IF NOT EXISTS (SELECT * FROM union_reporting.users where id = request_user_id AND (users.unit_coordinator = 1 OR users.head = 1)) THEN IF NOT EXISTS (SELECT * FROM union_reporting.customer where id = request_customer_id AND account_manager_id = request_user_id) THEN signal sqlstate '23515'; END IF; END IF;
INSERT INTO union_reporting.customer_account_members (`customer_id`, `user_id`) VALUES (request_customer_id, request_member_id); END
#
DROP procedure IF EXISTS `union_reporting`.`SELECT_CUSTOMER_MEMBER`;
#
CREATE PROCEDURE `union_reporting`.`SELECT_CUSTOMER_MEMBER`( In request_member_id varchar(11), In request_customer_id varchar(11), In request_user_id varchar(11) ) BEGIN IF NOT EXISTS (SELECT * FROM union_reporting.users where id = request_user_id AND (users.unit_coordinator = 1 OR users.head = 1 OR request_user_id = 1)) THEN IF NOT EXISTS (SELECT * FROM union_reporting.customer WHERE id = request_customer_id AND account_manager_id = request_user_id) THEN IF NOT EXISTS (SELECT * FROM union_reporting.customer_account_members WHERE customer_id = request_customer_id AND user_id = request_user_id) THEN signal sqlstate '23515'; END IF; END IF; END IF;
SELECT union_reporting.users.* FROM union_reporting.customer_account_members LEFT JOIN union_reporting.users ON union_reporting.customer_account_members.user_id = union_reporting.users.id WHERE (request_customer_id = '' OR customer_id=request_customer_id) AND (request_member_id = '' OR user_id=request_member_id); END
