DROP procedure IF EXISTS `union_reporting`.`SELECT_CUSTOMER`;
#
CREATE PROCEDURE `union_reporting`.`SELECT_CUSTOMER`( IN request_id VARCHAR(11), IN request_user_id VARCHAR(11) ) BEGIN
SELECT id, ANY_VALUE(name) as name, ANY_VALUE(coordinator_id) as coordinator_id, ANY_VALUE(accounting) as accounting, ANY_VALUE(account_manager_id) as account_manager_id, ANY_VALUE(created) as created, sum(proj_id_with_customer.proj_id IS NOT NULL) as projects_count FROM ( SELECT customer.*, projects.id as proj_id FROM union_reporting.customer LEFT JOIN union_reporting.projects ON customer.id=projects.customer_id UNION SELECT customer.*, projects.id as proj_id FROM union_reporting.customer RIGHT JOIN union_reporting.projects ON customer.id=projects.customer_id ) as proj_id_with_customer
WHERE proj_id_with_customer.id IS NOT NULL AND (request_id = '' OR id=request_id) AND (proj_id_with_customer.account_manager_id = request_user_id OR 1 = (SELECT head From union_reporting.users WHERE id = request_user_id) OR 1 = (SELECT unit_coordinator From union_reporting.users WHERE id = request_user_id) OR 1 = request_user_id OR (SELECT user_id FROM customer_account_members WHERE user_id = request_user_id AND customer_id = proj_id_with_customer.id)
) group by proj_id_with_customer.id;
END
