CREATE PROCEDURE `union_reporting`.`INSERT_TESTRUN`( IN request_user_id VARCHAR(10), IN testrun_id VARCHAR(10), IN testrun_build_name VARCHAR(500), IN testrun_starttime VARCHAR(500), IN testrun_execution_environment VARCHAR(500), IN testrun_milestone_id VARCHAR(10), IN testrun_test_suite_id VARCHAR(10), IN testrun_project_id VARCHAR(10), IN testrun_finishtime VARCHAR(500), IN testrun_author VARCHAR(500), IN testrun_debug VARCHAR(1) ) BEGIN IF NOT EXISTS (SELECT * FROM union_reporting.user_roles right join union_reporting.users on union_reporting.user_roles.user_id = id where id = request_user_id AND (users.manager = 1 OR (union_reporting.user_roles.project_id = testrun_project_id AND (user_roles.admin = 1 OR user_roles.manager = 1 OR user_roles.engineer = 1)))) THEN signal sqlstate '23515'; END IF;
INSERT INTO union_reporting.test_runs ( id, build_name, start_time, milestone_id, test_suite_id, project_id, execution_environment, finish_time, author, debug) VALUES ( IF(testrun_id = '', null,testrun_id), testrun_build_name, if(testrun_starttime = '', FROM_UNIXTIME('0'), FROM_UNIXTIME(testrun_starttime)), IF(testrun_milestone_id = '', null, testrun_milestone_id), IF(testrun_test_suite_id = '', null, testrun_test_suite_id), IF(testrun_project_id = '', 0, testrun_project_id), IF(testrun_execution_environment = '', null, testrun_execution_environment), If(testrun_finishtime = '', null, FROM_UNIXTIME(testrun_finishtime)), IF(testrun_author = '', null, testrun_author), IF(testrun_debug = '', '0', testrun_debug) ) ON DUPLICATE KEY UPDATE build_name = IF(testrun_build_name = '', build_name, testrun_build_name), start_time = IF(testrun_starttime = '', start_time, FROM_UNIXTIME(testrun_starttime)), milestone_id = IF(testrun_milestone_id = '', milestone_id, IF(testrun_milestone_id = '0', null, testrun_milestone_id)), test_suite_id = IF(testrun_test_suite_id = '', test_suite_id, testrun_test_suite_id), project_id = IF(testrun_project_id = '',project_id, testrun_project_id ), execution_environment = IF(testrun_execution_environment = '', execution_environment, IF(testrun_execution_environment = '$blank', '', testrun_execution_environment)), finish_time = If(testrun_finishtime = '', finish_time, FROM_UNIXTIME(testrun_finishtime)), author = IF(testrun_author = '', author, IF(testrun_author = '$blank', '', testrun_author)), debug = IF(testrun_debug = '', debug, testrun_debug);
SET @result_id = IF(testrun_id = '', LAST_INSERT_ID(), testrun_id);
SELECT * from union_reporting.test_runs where id = @result_id; END

DROP procedure IF EXISTS `union_reporting`.`INSERT_AUDIT`;
#
CREATE PROCEDURE `union_reporting`.`INSERT_AUDIT`( In audit_id varchar(11), In audit_project_id varchar(11), In audit_status_id varchar(11), In audit_created varchar(100), In audit_started varchar(100), In audit_progress_finished varchar(100), In audit_due_date varchar(100), In audit_submitted varchar(100), In audit_result varchar(3), In audit_summary mediumtext, In audit_service_type_id varchar(11), IN user_id VARCHAR(11) ) BEGIN Declare AA INT(1); SET AA = (SELECT audit_admin From union_reporting.users WHERE id = user_id);
IF (1!=(SELECT audit_admin From union_reporting.users WHERE id = user_id) AND !EXISTS(SELECT * From union_reporting.auditor_assignments WHERE user_id = user_id AND audit_id=audit_id)) THEN CALL raise_error; END IF;
INSERT INTO union_reporting.audits (`id`, `project_id`, `started`, `progress_finished`, `due_date`, `submitted`, `result`, `summary`, `service_type_id`) VALUES ( IF(audit_id='',null,audit_id), audit_project_id, IF(audit_started='',null,FROM_UNIXTIME(audit_started)), IF(audit_progress_finished='',null,FROM_UNIXTIME(audit_progress_finished)), IF(audit_due_date='',DATE_ADD((select submitted from (select max(submitted) as submitted from union_reporting.audits where project_id = audit_project_id) as `tmp`), INTERVAL +6 MONTH ),FROM_UNIXTIME(audit_due_date)), IF(audit_submitted='',null,FROM_UNIXTIME(audit_submitted)), IF(audit_result='',null,audit_result), IF(audit_summary='',null,audit_summary), IF(audit_service_type_id='', 1, audit_service_type_id) ) ON DUPLICATE KEY UPDATE status_id = IF(audit_status_id='' OR (audit_status_id = 4 AND AA = 0),status_id,audit_status_id), result = IF(audit_result='',result,audit_result), created = IF(audit_created='',created,FROM_UNIXTIME(audit_created)), started = IF(audit_started='',started,FROM_UNIXTIME(audit_started)), progress_finished = IF(audit_progress_finished='',progress_finished,FROM_UNIXTIME(audit_progress_finished)), due_date = IF(audit_due_date!='' AND AA=1,FROM_UNIXTIME(audit_due_date),due_date), submitted = IF(audit_submitted='',submitted,FROM_UNIXTIME(audit_submitted)), summary = IF(audit_summary='',summary,IF(audit_summary='$blank', null, audit_summary)), service_type_id = IF(audit_service_type_id='',service_type_id, audit_service_type_id) ;
Select * from union_reporting.audits Where id=LAST_INSERT_ID();
END

