CREATE PROCEDURE `union_reporting`.`SELECT_TESTRESULT`( IN request_user_id VARCHAR(10), IN testresult_project_id VARCHAR(10), IN testresult_Id VARCHAR(10), IN testresult_test_id VARCHAR(10), IN testresult_final_result_id VARCHAR(10), IN testresult_comment VARCHAR(5000), IN testresult_test_run_id VARCHAR(10), IN testresult_resolution_id VARCHAR(10), IN testresult_debug VARCHAR(1), IN resultFailReason mediumtext, In resultsNumber INT(10) ) BEGIN IF NOT EXISTS (SELECT * FROM union_reporting.user_roles right join union_reporting.users on union_reporting.user_roles.user_id = id where id = request_user_id AND (users.manager = 1 OR users.auditor = 1 OR users.audit_admin = 1 OR users.account_manager = 1 OR users.unit_coordinator = 1 OR users.head = 1 OR (union_reporting.user_roles.project_id = testresult_project_id AND (user_roles.admin = 1 OR user_roles.manager = 1 OR user_roles.engineer = 1 OR user_roles.viewer = 1)))) THEN signal sqlstate '23515'; END IF;
SELECT * FROM union_reporting.test_results as res WHERE (testresult_Id = '' OR id=testresult_Id) AND (testresult_project_id = '' OR project_id = testresult_project_id) AND (testresult_test_id = '' OR test_id=testresult_test_id) AND (testresult_final_result_id = '' OR final_result_id=testresult_final_result_id) AND (testresult_comment = '' OR comment=testresult_comment) AND (testresult_test_run_id = '' OR test_run_id=testresult_test_run_id) AND (testresult_resolution_id = '' OR test_resolution_id=testresult_resolution_id) AND (testresult_debug = '' OR debug=testresult_debug) AND (resultFailReason = '' OR REPLACE(REPLACE(REPLACE(REPLACE(fail_reason, '\r', ''), '\n', ''), CHAR(9), ''),' ','') LIKE CONCAT('%',REPLACE(REPLACE(REPLACE(REPLACE(resultFailReason, '\r', ''), '\n', ''), CHAR(9), ''),' ',''),'%')) ORDER BY start_date DESC LIMIT resultsNumber ; END

DROP procedure IF EXISTS `union_reporting`.`INSERT_TESTRESULT`;
#
CREATE PROCEDURE `union_reporting`.`INSERT_TESTRESULT`( IN request_user_id VARCHAR(10), IN testresult_project_id VARCHAR(10), IN result_id VARCHAR(10), IN result_test_id VARCHAR(500), IN result_final_result_id VARCHAR(500), IN result_comment text, IN result_test_run_id VARCHAR(500), IN result_test_resolution_id VARCHAR(10), IN result_test_log longtext, IN result_test_debug VARCHAR(1), IN result_finishtime VARCHAR(500), IN result_starttime VARCHAR(500), IN result_finalresult_updated VARCHAR(500), IN result_finalresult_fail_reason mediumtext, IN result_test_assignee VARCHAR(10) ) BEGIN IF NOT EXISTS (SELECT * FROM union_reporting.user_roles right join union_reporting.users on union_reporting.user_roles.user_id = id where id = request_user_id AND (users.manager = 1 OR (union_reporting.user_roles.project_id = testresult_project_id AND (user_roles.admin = 1 OR user_roles.manager = 1 OR user_roles.engineer = 1)))) THEN signal sqlstate '23515'; END IF;
INSERT INTO union_reporting.test_results (project_id, id, test_id, final_result_id, comment, test_run_id, test_resolution_id, log, debug, start_date, finish_date, final_result_updated, fail_reason, assignee) VALUES ( testresult_project_id, IF(result_id='', null, result_id), result_test_id, IF(result_final_result_id='',1,result_final_result_id), IF(result_comment = '', null, result_comment), IF(result_test_run_id = '', null, result_test_run_id), IF(result_test_resolution_id = '', 1, result_test_resolution_id), IF(result_test_log = '', null, result_test_log), IF(result_test_debug = '', 0, result_test_debug), If(result_starttime = '', null, FROM_UNIXTIME(result_starttime)), If(result_finishtime = '', null, FROM_UNIXTIME(result_finishtime)), NOW(), IF(result_finalresult_fail_reason = '', null, result_finalresult_fail_reason), IF(result_test_assignee = '', null, result_test_assignee)) ON DUPLICATE KEY UPDATE test_id=IF(result_test_id = '',test_id,result_test_id), final_result_id = IF(result_final_result_id = '', final_result_id, result_final_result_id), comment = IF(result_comment = '', IF(comment = null OR '', null, comment), IF(result_comment='$blank', null, result_comment)), test_resolution_id = IF(final_result_id = 2, 1, IF(result_test_resolution_id='', test_resolution_id, result_test_resolution_id)), log = IF(result_test_log = '', log, IF(result_test_log = '$blank', '', result_test_log)), debug = IF(result_test_debug = '', debug, result_test_debug), start_date = If(result_starttime = '', start_date, FROM_UNIXTIME(result_starttime)), finish_date = If(result_finishtime = '', finish_date, FROM_UNIXTIME(result_finishtime)), final_result_updated = IF(result_final_result_id = final_result_id AND result_finalresult_updated = '', NOW(), IF(result_finalresult_updated = '',final_result_updated, FROM_UNIXTIME(result_finalresult_updated))), fail_reason = IF(result_finalresult_fail_reason = '', fail_reason, IF(result_finalresult_fail_reason = '$blank', '', result_finalresult_fail_reason)), assignee = IF(result_test_assignee = '', null, result_test_assignee) ; END
