CREATE TABLE `issue_status` (
`id` INT(11) NOT NULL AUTO_INCREMENT,
`color` INT(1) NOT NULL,
`name` VARCHAR(50) NOT NULL,
PRIMARY KEY (`id`),
UNIQUE INDEX `id_UNIQUE` (`id` ASC),
UNIQUE INDEX `name_UNIQUE` (`name` ASC));
