DROP procedure IF EXISTS `SELECT_APP_SETTINGS`;

#

CREATE PROCEDURE `SELECT_APP_SETTINGS`()
BEGIN
SELECT * FROM app_settings;
END

DROP procedure IF EXISTS `INSERT_APP_SETTINGS`;
#
CREATE PROCEDURE `INSERT_APP_SETTINGS`( IN request_id VARCHAR(11), IN request_ldap_auth VARCHAR(1), IN request_base_auth VARCHAR(1), IN request_audits VARCHAR(1) ) BEGIN INSERT INTO app_settings (audits, ldap_auth, base_auth, id) VALUES ( request_audits, request_ldap_auth, request_base_auth, request_id )
ON DUPLICATE KEY UPDATE ldap_auth= IF(request_ldap_auth = '', ldap_auth, request_ldap_auth), base_auth = IF(request_base_auth = '', base_auth, request_base_auth), audits = IF(request_audits = '', audits, request_audits) ;
Select * from app_settings Where id=(Select MAX(id) from app_settings); END

DROP procedure IF EXISTS `SELECT_USERS`;
#
CREATE PROCEDURE `SELECT_USERS`( IN request_id VARCHAR(10), IN request_first_name VARCHAR(100), IN request_second_name VARCHAR(100), IN request_user_name VARCHAR(100), IN request_unit_coordinator VARCHAR(11), IN request_account_manager VARCHAR(11) ) BEGIN SELECT users.id as id, first_name, second_name, user_name, updated, password, session_code, sessions.created as session_created, admin, manager, auditor, audit_admin, email, ldap_user, unit_coordinator, audit_notifications, head, account_manager FROM union_reporting.users as users LEFT JOIN union_reporting.user_sessions as sessions ON users.last_session_id = sessions.id WHERE (request_id= '' OR users.id=request_id) AND (request_first_name= '' OR first_name=request_first_name) AND (request_second_name = '' OR second_name=request_second_name) AND (request_user_name = '' OR user_name=request_user_name) AND (request_unit_coordinator ='' OR request_unit_coordinator=unit_coordinator) AND (request_account_manager ='' OR request_account_manager=account_manager) ; END

DROP procedure IF EXISTS `INSERT_USER`;
#
CREATE PROCEDURE `INSERT_USER`( In request_id varchar(10), IN request_user_name VARCHAR(100), IN request_first_name VARCHAR(100), IN request_second_name VARCHAR(100), IN request_password VARCHAR(100), IN request_admin VARCHAR(1), IN request_manager VARCHAR(1), IN request_auditor VARCHAR(1), IN request_audit_admin VARCHAR(1), IN request_unit_coordinator VARCHAR(1), IN request_email VARCHAR(150), IN request_ldap_user VARCHAR(1), IN request_head VARCHAR(1), IN request_account_manager VARCHAR(1), IN request_audit_notifications VARCHAR(1) ) BEGIN INSERT INTO union_reporting.users (id, first_name, second_name, user_name, pass, admin, manager, auditor, audit_admin, email, ldap_user,unit_coordinator,audit_notifications,head,account_manager) VALUES ( IF(request_id='',null,request_id), request_first_name, request_second_name, request_user_name, request_password, IF(request_admin='',0,request_admin), IF(request_manager='',0,request_manager), IF(request_auditor='',0,request_auditor), IF(request_audit_admin='',0,request_audit_admin), request_email, IF(request_ldap_user='',0,request_ldap_user), IF(request_unit_coordinator='',0,request_unit_coordinator), IF(request_audit_notifications='',1,request_audit_notifications), IF(request_head='',0,request_head), IF(request_account_manager='',0,request_account_manager) ) ON DUPLICATE KEY UPDATE user_name = IF(request_user_name = '', user_name, request_user_name), first_name = IF(request_first_name = '', first_name, request_first_name), second_name = IF(request_second_name = '', second_name, request_second_name), pass = IF(request_password = '', pass, request_password), admin = IF(request_admin = '', admin, request_admin), manager = IF(request_manager = '', manager, request_manager), auditor = IF(request_auditor='', auditor, request_auditor), audit_admin = IF(request_audit_admin='',audit_admin,request_audit_admin), email = IF(request_email='', email, request_email), ldap_user = IF(request_ldap_user='', ldap_user, request_ldap_user), head = IF(request_head='', head, request_head), account_manager = IF(request_account_manager='', account_manager, request_account_manager), unit_coordinator = IF(request_unit_coordinator='',unit_coordinator,request_unit_coordinator), audit_notifications = IF(request_audit_notifications='',audit_notifications,request_audit_notifications) ;
SET @audit_id = IF(request_id = '', (SELECT LAST_INSERT_ID()), request_id);
SELECT * from union_reporting.audits where id = @audit_id;
END
