ALTER TABLE `users` CHANGE COLUMN `pass` `password` VARCHAR(150) NOT NULL ;
#
DROP TRIGGER IF EXISTS users_BEFORE_UPDATE
#
CREATE TRIGGER `users_BEFORE_UPDATE` BEFORE UPDATE ON `users` FOR EACH ROW BEGIN
IF NEW.id = 1 AND ( NEW.user_name != OLD.user_name OR NEW.first_name != OLD.first_name OR NEW.second_name != OLD.second_name OR NEW.password != OLD.password OR NEW.email != OLD.email OR NEW.admin != OLD.admin OR NEW.manager != OLD.manager OR NEW.auditor != OLD.auditor OR NEW.audit_admin != OLD.audit_admin) THEN SIGNAL SQLSTATE '45000' SET MESSAGE_TEXT = 'This user is not editable!'; END IF;
IF (NEW.id = '' OR NEW.id = null) THEN SIGNAL SQLSTATE '45000' SET MESSAGE_TEXT = 'This user is already created!'; END IF; END

DROP procedure IF EXISTS `INSERT_SESSION`;

