DROP procedure IF EXISTS `INSERT_AUDIT`;
#
CREATE PROCEDURE `INSERT_AUDIT`( In request_id varchar(11), In request_project_id varchar(11), In request_status_id varchar(11), In request_created varchar(100), In request_started varchar(100), In request_progress_finished varchar(100), In request_due_date varchar(100), In request_submitted varchar(100), In request_result varchar(3), In request_summary mediumtext, In request_service_id varchar(11)) BEGIN
INSERT INTO audits (`id`, `project_id`, `started`, `progress_finished`, `due_date`, `submitted`, `result`, `summary`, `service_type_id`) VALUES ( IF(request_id='',null,request_id), request_project_id, IF(request_started='',null,FROM_UNIXTIME(request_started)), IF(request_progress_finished='',null,FROM_UNIXTIME(request_progress_finished)), IF(request_due_date='',DATE_ADD((select submitted from (select max(submitted) as submitted from audits where project_id = request_project_id) as `tmp`), INTERVAL +6 MONTH ),FROM_UNIXTIME(request_due_date)), IF(request_submitted='',null,FROM_UNIXTIME(request_submitted)), IF(request_result='',null,request_result), IF(request_summary='',null,request_summary), IF(request_service_id='', 1, request_service_id) ) ON DUPLICATE KEY UPDATE status_id = IF(request_status_id='' OR (request_status_id = 4 AND AA = 0),status_id,request_status_id), result = IF(request_result='',result,request_result), created = IF(request_created='',created,FROM_UNIXTIME(request_created)), started = IF(request_started='',started,FROM_UNIXTIME(request_started)), progress_finished = IF(request_progress_finished='',progress_finished,FROM_UNIXTIME(request_progress_finished)), due_date = IF(request_due_date!='' AND AA=1,FROM_UNIXTIME(request_due_date),due_date), submitted = IF(request_submitted='',submitted,FROM_UNIXTIME(request_submitted)), summary = IF(request_summary='',summary,IF(request_summary='$blank', null, request_summary)), service_type_id = IF(request_service_id='',service_type_id, request_service_id) ;
SET @audit_id = IF(request_id = '', (SELECT LAST_INSERT_ID()), request_id); SELECT * from audits where id = @audit_id;
END

