CREATE PROCEDURE `SELECT_PROJECT`( IN request_name VARCHAR(500), IN request_id VARCHAR(10), IN request_customer_id VARCHAR(11), IN request_user_id VARCHAR(11) ) BEGIN SELECT DISTINCT proj.* FROM projects as proj LEFT JOIN user_roles as us_r ON proj.id = us_r.project_id LEFT JOIN customer as cus_r ON proj.customer_id = cus_r.id WHERE (request_id = '' OR proj.id=request_id) AND (request_name = '' OR proj.name=request_name) AND (request_customer_id = '' OR proj.customer_id=request_customer_id) AND (request_user_id = '' OR user_id=request_user_id OR 1=(SELECT admin From users WHERE id = request_user_id) OR 1=(SELECT head From users WHERE id = request_user_id) OR 1=(SELECT unit_coordinator From users WHERE id = request_user_id) OR 1=(SELECT manager From users WHERE id = request_user_id) OR 1=(SELECT auditor From users WHERE id = request_user_id) OR 1=(SELECT audit_admin From users WHERE id = request_user_id) OR account_manager_id = request_user_id ); END

