DROP procedure IF EXISTS `SELECT_TEST_RUN_STATS`;
# DROP procedure IF EXISTS `SELECT_TESTRUN_STATS`;
#
CREATE PROCEDURE `SELECT_TEST_RUN_STATS`( IN request_testrun_id VARCHAR(10), IN request_build_name VARCHAR(500), IN request_milestone_id VARCHAR(10), IN request_test_suite_id VARCHAR(10), IN request_project_id VARCHAR(10), IN request_execution_environment VARCHAR(500), IN request_debug VARCHAR(1) ) BEGIN SELECT trn.id, trn.build_name, trn.project_id, trn.test_suite_id, trn.start_time, trn.finish_time, sum(frs.color = 1) as failed, sum(frs.color = 5) as passed, sum(frs.color = 3) as not_executed, sum(frs.color = 2) as in_progress, sum(frs.color = 4) as pending, sum(frs.color != 0) as total, sum(rr.color = 1 AND frs.color != 5) as app_issue, sum(rr.color = 2 AND frs.color != 5) as warning, sum(rr.color = 3 AND frs.color != 5) as not_assigned, sum((rr.color = 4 OR rr.color = 5) AND frs.color != 5) as other
from union_reporting.test_runs as trn right join union_reporting.test_results as trs on trn.id=test_run_id left join union_reporting.final_results as frs on trs.final_result_id = frs.id left join union_reporting.result_resolution as rr on trs.test_resolution_id = rr.id
WHERE (request_testrun_id= '' OR trn.id=request_testrun_id) AND (request_build_name = '' OR build_name=request_build_name) AND (request_milestone_id = '' OR milestone_id=request_milestone_id) AND (request_test_suite_id = '' OR test_suite_id=request_test_suite_id) AND (request_project_id = '' OR trn.project_id=request_project_id) AND (request_execution_environment = '' OR execution_environment=request_execution_environment) AND (request_debug = '' OR trn.debug=request_debug)
GROUP BY trn.id ORDER BY finish_time DESC;
END

