CREATE PROCEDURE `INSERT_PROJECT_USER`( IN request_user_id varchar(10), IN request_project_id VARCHAR(10), IN request_admin VARCHAR(1), IN request_manager VARCHAR(1), IN request_engineer VARCHAR(1) ) BEGIN INSERT INTO user_roles (`user_id`, `project_id`, `admin`, `manager`, `engineer`) VALUES ( request_user_id, request_project_id, IF(request_admin='',0,request_admin), IF(request_manager='',0,request_manager), IF(request_engineer='',0,request_engineer) ) ON DUPLICATE KEY UPDATE admin = IF(request_admin = '', admin, request_admin), manager = IF(request_manager = '', manager, request_manager), engineer = IF(request_engineer = '', engineer, request_engineer) ;
SELECT * from user_roles where user_id = request_user_id and project_id = request_project_id;
END

