CREATE PROCEDURE `SELECT_RESULT_RESOLUTION`( IN request_project_id VARCHAR(11), IN request_id VARCHAR(11) ) BEGIN SELECT * FROM result_resolution WHERE ((request_project_id = '' OR project_id = request_project_id) OR project_id is null) AND (request_id = '' OR id = request_id); END

DROP procedure IF EXISTS `SELECT_TEST_STATS`;
#
CREATE PROCEDURE `SELECT_TEST_STATS`(IN request_id VARCHAR(10) ) BEGIN SELECT id, name, developer_id, total_runs, passed, failed, app_issue, autotest_issue, resolution_na FROM tests LEFT JOIN (SELECT tr.test_id, SUM(IF(final_result_id != 3, 1, 0)) AS total_runs, SUM(IF(final_result_id = 1, 1, 0)) AS failed, SUM(IF(final_result_id = 2, 1, 0)) AS passed, SUM(IF(color = 1, 1, 0)) AS app_issue, SUM(IF(color = 2, 1, 0)) AS autotest_issue, SUM(IF(color = 3 AND final_result_id != 2, 1, 0)) AS resolution_na FROM test_results tr INNER JOIN result_resolution AS rr ON test_resolution_id = rr.id GROUP BY test_id) AS result ON tests.id = result.test_id Where (request_id = '' OR id IN (select test_id from suite_tests where suite_id = request_id)); END

DROP procedure IF EXISTS `INSERT_TEST_TO_TESTSUITE`;
#
CREATE PROCEDURE `INSERT_TEST_TO_TESTSUITE`( IN request_test_id VARCHAR(11), IN request_suite_id VARCHAR(11) ) BEGIN IF NOT EXISTS (SELECT * FROM suite_tests where test_id = request_test_id AND suite_id = request_suite_id) THEN INSERT INTO suite_tests (test_id, suite_id) VALUES (request_test_id, request_suite_id); END IF; END

DROP procedure IF EXISTS `REMOVE_TEST_FROM_TESTSUITE`;
#
CREATE PROCEDURE `REMOVE_TEST_FROM_TESTSUITE`( IN request_test_id VARCHAR(11), IN request_suite_id VARCHAR(11) ) BEGIN IF EXISTS (SELECT * FROM suite_tests where test_id = request_test_id AND suite_id = request_suite_id) THEN DELETE FROM suite_tests WHERE test_id = request_test_id AND suite_id = request_suite_id; END IF; END

DROP procedure IF EXISTS `SELECT_TEST_SUITES`;
#
CREATE PROCEDURE `SELECT_TEST_SUITES`(IN request_test_id VARCHAR(11)) BEGIN Select test_suites.* FROM suite_tests inner join test_suites on suite_id = test_suites.id where test_id = request_test_id; END

