CREATE PROCEDURE `SELECT_TEST_SUITES`( IN request_test_id VARCHAR(11), IN request_suite_id VARCHAR(11) ) BEGIN Select * FROM suite_tests where (request_test_id = '' OR test_id = request_test_id) AND (request_suite_id = '' OR suite_id = request_suite_id); END

DROP procedure IF EXISTS `INSERT_PASSWORD`;

#

CREATE PROCEDURE `INSERT_PASSWORD`(
IN request_user_id VARCHAR(11),
IN request_old_password VARCHAR(150),
IN request_password VARCHAR(150)
)
BEGIN
IF(request_old_password != (SELECT password From users WHERE id = request_user_id))
THEN
SIGNAL SQLSTATE '23516'
SET MESSAGE_TEXT = 'Old password is wrong!';
END IF;

UPDATE users
SET password = request_password
WHERE id = request_user_id;

select * from users where id = request_user_id;
END

ALTER TABLE `test_runs` ADD COLUMN `ci_build` VARCHAR(500) NULL AFTER `label_id`;
#
DROP procedure IF EXISTS `INSERT_TEST_RUN`;
#
CREATE PROCEDURE `INSERT_TEST_RUN`( IN request_id VARCHAR(10), IN request_build_name VARCHAR(500), IN request_start_time VARCHAR(500), IN request_execution_environment VARCHAR(500), IN request_milestone_id VARCHAR(10), IN request_test_suite_id VARCHAR(10), IN request_project_id VARCHAR(10), IN request_finish_time VARCHAR(500), IN request_author VARCHAR(500), IN request_label_id VARCHAR(11), IN request_debug VARCHAR(1), IN request_ci_build VARCHAR(500) ) BEGIN INSERT INTO test_runs ( id, build_name, start_time, milestone_id, test_suite_id, project_id, execution_environment, finish_time, author, label_id, debug, ci_build) VALUES ( IF(request_id = '', null,request_id), request_build_name, if(request_start_time = '', FROM_UNIXTIME('0'), FROM_UNIXTIME(request_start_time)), IF(request_milestone_id = '', null, request_milestone_id), IF(request_test_suite_id = '', null, request_test_suite_id), IF(request_project_id = '', 0, request_project_id), IF(request_execution_environment = '', execution_environment, IF(request_execution_environment = '$blank', '', request_execution_environment)), If(request_finish_time = '', null, FROM_UNIXTIME(request_finish_time)), IF(request_author = '', null, request_author), IF(request_label_id = '', 1, request_label_id), IF(request_debug = '', '0', request_debug), request_ci_build ) ON DUPLICATE KEY UPDATE build_name = IF(request_build_name = '', build_name, request_build_name), start_time = IF(request_start_time = '', start_time, FROM_UNIXTIME(request_start_time)), milestone_id = IF(request_milestone_id = '', milestone_id, IF(request_milestone_id = '0', null, request_milestone_id)), test_suite_id = IF(request_test_suite_id = '', test_suite_id, request_test_suite_id), project_id = IF(request_project_id = '',project_id, request_project_id ), execution_environment = IF(request_execution_environment = '', execution_environment, IF(request_execution_environment = '$blank', '', request_execution_environment)), finish_time = If(request_finish_time = '', finish_time, FROM_UNIXTIME(request_finish_time)), author = IF(request_author = '', author, IF(request_author = '$blank', '', request_author)), label_id = IF(request_label_id = '', label_id, request_label_id), debug = IF(request_debug = '', debug, request_debug), ci_build = IF(request_ci_build = '', ci_build, request_ci_build);
SET @result_id = IF(request_id = '', LAST_INSERT_ID(), request_id); SELECT * from test_runs where id = @result_id; END

DROP procedure IF EXISTS `SELECT_TEST_RUN_STATS`;
#
CREATE PROCEDURE `SELECT_TEST_RUN_STATS`( IN request_testrun_id VARCHAR(10), IN request_test_suite_id VARCHAR(10), IN request_project_id VARCHAR(10), IN request_debug VARCHAR(1) ) BEGIN SELECT id, build_name, project_id, test_suite_id, start_time, finish_time, failed, passed, not_executed, in_progress, pending, total, app_issue, warning, not_assigned, other
from test_runs left join testrun_statistic on id = testrun_id WHERE (request_testrun_id= '' OR id=request_testrun_id) AND (request_test_suite_id = '' OR test_suite_id=request_test_suite_id) AND (request_project_id = '' OR project_id=request_project_id) AND (request_debug = '' OR debug=request_debug) ORDER BY finish_time DESC;
END

CREATE TABLE `imports` ( `id` INT NOT NULL AUTO_INCREMENT, `testrun_id` INT NULL, `is_finished` INT(1) NOT NULL DEFAULT 0, `started` DATETIME NULL, `finished` DATETIME NULL, `log` LONGTEXT NULL, PRIMARY KEY (`id`), UNIQUE INDEX `id_UNIQUE` (`id` ASC), INDEX `imports_testrun_table_idx` (`testrun_id` ASC), CONSTRAINT `imports_testrun_table` FOREIGN KEY (`testrun_id`) REFERENCES `test_runs` (`id`) ON DELETE CASCADE ON UPDATE CASCADE);
#
ALTER TABLE `imports` ADD COLUMN `project_id` INT NOT NULL AFTER `testrun_id`, ADD INDEX `imports_project_idx` (`project_id` ASC);
#
ALTER TABLE `imports` ADD CONSTRAINT `imports_project` FOREIGN KEY (`project_id`) REFERENCES `projects` (`id`) ON DELETE CASCADE ON UPDATE CASCADE;

DROP procedure IF EXISTS `INSERT_IMPORT`;
#
CREATE PROCEDURE `INSERT_IMPORT`( IN request_id VARCHAR(11), IN request_testrun_id VARCHAR(11), IN request_project_id VARCHAR(11), IN request_is_finished VARCHAR(1), IN request_started VARCHAR(500), IN request_finished VARCHAR(500), IN request_log LONGTEXT ) BEGIN
SET time_zone = '+00:00'; INSERT INTO imports (id, testrun_id, project_id, is_finished, started, finished, log) VALUES ( IF(request_id = '', null,request_id), IF(request_testrun_id = '', null,request_testrun_id), IF(request_project_id = '', null,request_project_id), IF(request_is_finished = '', null,request_is_finished), IF(request_started = '', null,FROM_UNIXTIME(request_started)), IF(request_finished = '', null,FROM_UNIXTIME(request_finished)), IF(request_log = '', null,request_log) ) ON DUPLICATE KEY UPDATE testrun_id = IF(request_testrun_id = '', testrun_id, request_testrun_id), project_id = IF(request_project_id = '', project_id, request_project_id), is_finished = IF(request_is_finished = '', is_finished, request_is_finished), started = IF(request_started = '', started, FROM_UNIXTIME(request_started)), finished= IF(request_finished = '', finished, FROM_UNIXTIME(request_finished)), log = IF(request_log = '', log, request_log);
SET @result_id = IF(request_id = '', LAST_INSERT_ID(), request_id); SELECT * from imports where id = @result_id; END

DROP procedure IF EXISTS `SELECT_IMPORTS`;
#
CREATE PROCEDURE `SELECT_IMPORTS`( In request_id varchar(11), In request_project_id varchar(11), In request_testrun_id varchar(11) ) BEGIN SELECT * FROM imports WHERE (request_id = '' OR id=request_id) AND (request_project_id = '' OR project_id=request_project_id) AND (request_testrun_id = '' OR testrun_id=request_testrun_id); END

DROP procedure IF EXISTS `SELECT_TEST_RESULT`;
#
CREATE PROCEDURE `SELECT_TEST_RESULT`( IN request_project_id VARCHAR(10), IN request_id VARCHAR(10), IN request_test_id VARCHAR(10), IN request_final_result_id VARCHAR(10), IN request_test_run_id VARCHAR(10), IN request_test_resolution_id VARCHAR(10), IN request_debug VARCHAR(1), In request_limit INT(10) ) BEGIN SELECT * FROM test_results as res WHERE (request_id = '' OR id=request_id) AND (request_project_id = '' OR project_id = request_project_id) AND (request_test_id = '' OR test_id=request_test_id) AND (request_final_result_id = '' OR final_result_id=request_final_result_id) AND (request_test_run_id = '' OR test_run_id=request_test_run_id) AND (request_test_resolution_id = '' OR test_resolution_id=request_test_resolution_id) AND (request_debug = '' OR debug=request_debug) ORDER BY start_date DESC LIMIT request_limit ; END
